import {React, useState, useEffect} from 'react'
import { KeyboardAvoidingView, StyleSheet, Text, TextInput, TouchableOpacity, View } from 'react-native'
// import { auth } from '../../../firebase';
import {useNavigation} from '@react-navigation/core'
import { getAuth, createUserWithEmailAndPassword, signInWithEmailAndPassword } from 'firebase/auth';
import {initializeApp} from "firebase/app"
// import {app, auth} from '../../../firebase'


const firebaseConfig = {
  apiKey: "AIzaSyBI7Lal6j6hkoOXMDRISpXlV1bAuqj3AzU",
  authDomain: "nimble-auth-87b5f.firebaseapp.com",
  projectId: "nimble-auth-87b5f",
  storageBucket: "nimble-auth-87b5f.appspot.com",
  messagingSenderId: "910642886814",
  appId: "1:910642886814:web:0bc627705af2154416e38f"
};

initializeApp(firebaseConfig)
const LoginScreen = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const navigation = useNavigation();

  useEffect(()=>{
  const auth = getAuth()
  const unsubscribe = auth.onAuthStateChanged(user => {
      if (user) {
        navigation.navigate('Index')
      }
    })

    return unsubscribe
  }, [])


  const handleSignUp = () => {
    const auth = getAuth()
    createUserWithEmailAndPassword(auth, email, password)
    .then(userCredentials => {
      const user = userCredentials.user; 
      console.log('Registered with: ', user.email);
    })
    .catch(error => alert(error.message))
  }

  const handleLogin = () => {
    const auth = getAuth()
    signInWithEmailAndPassword(auth, email, password)
    .then(userCredentials => {
      const user = userCredentials.user; 
      console.log('Logged in with: ', user.email);
    })
    .catch(error => alert(error.message))
  }

  return (

    <KeyboardAvoidingView
    style={styles.container}
    behavior="padding">

      <View style={styles.inputContainer}>
          <TextInput  placeholder='Email'
          value={email}
          onChangeText={text => setEmail(text)}
          style={styles.input}
            />

        <TextInput  placeholder='Password'
          value={password}
          onChangeText={text => setPassword(text)}
          style={styles.input}
          secureTextEntry
        />

      </View>

      <View style={styles.buttonContainer}>
        <TouchableOpacity 
        onPress={handleLogin}
        style={styles.button} >

          <Text style={styles.button}>Log in</Text>

        </TouchableOpacity>


        <TouchableOpacity
         onPress={handleSignUp}
        style={[styles.button, styles.buttonOutline]} >

          <Text style={styles.buttonOutlineText}>Register</Text>

        </TouchableOpacity>
        </View>

    </KeyboardAvoidingView>


  )
}

export default LoginScreen;


const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  inputContainer: {
    width: '80%'
  },
  input: {
    backgroundColor: 'white',
    paddingHorizontal: 15,
    paddingVertical: 10,
    borderRadius: 10,
    marginTop: 5,
  },
  buttonContainer: {
    width: '60%',
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 40,
  },
  button: {
    backgroundColor: '#0782F9',
    width: '100%',
    padding: 15,
    borderRadius: 10,
    alignItems: 'center',
  },
  buttonOutline: {
    backgroundColor: 'white',
    marginTop: 5,
    borderColor: '#0782F9',
    borderWidth: 2,
  },
  buttonText: {
    color: 'white',
    fontWeight: '700',
    fontSize: 16,
  },
  buttonOutlineText: {
    color: '#0782F9',
    fontWeight: '700',
    fontSize: 16,
  },
});
