    'worklet';
    const propsToCheck = [prop];
    while (propsToCheck.length > 0) {
        const currentProp = propsToCheck.pop();
        if (Array.isArray(currentProp)) {
            for (const item of currentProp) {
                propsToCheck.push(item);
            }
